package Operator;
import java.io.*;
public class ArithoperatorExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=3;
		int a= i++;
		int b= ++i;
		System.out.println(a);
		System.out.println(b);
		
		System.out.println(10<<1);
		
		System.out.println(10>>1);
		
	}

}
